package com.shata.hand_pose_estimation_app.Activity.Patient.Fragment;

import android.annotation.SuppressLint;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Toast;

import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;

import com.shata.hand_pose_estimation_app.Adapters.Adapter_All_Photos;
import com.shata.hand_pose_estimation_app.Models.ModelPatient;
import com.shata.hand_pose_estimation_app.Models.ModelPatientImageHand;
import com.shata.hand_pose_estimation_app.R;
import com.shata.hand_pose_estimation_app.databinding.FragmentPatientProgressBinding;

import java.util.ArrayList;
import java.util.List;

public class ProgressPatientFragment extends Fragment implements Adapter_All_Photos.OnItemClickListener {

    FragmentPatientProgressBinding patientProgressBinding;
    Adapter_All_Photos adapterAllPhotos;
    ModelPatient patient;
    private List<ModelPatientImageHand> mAllImageHands;

    public ProgressPatientFragment() {
    }

    public ProgressPatientFragment(ModelPatient patient) {
        this.patient = patient;
    }

    @SuppressLint("UseCompatLoadingForDrawables")
    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment

        patientProgressBinding = FragmentPatientProgressBinding.inflate(getLayoutInflater(), container, false);
        patientProgressBinding.seeAll.setOnClickListener(v -> {
            Toast.makeText(getActivity(), "See All Image", Toast.LENGTH_SHORT).show();
        });

        patientProgressBinding.patientRecyclerViewPhotos.setLayoutManager(new LinearLayoutManager(getActivity(), LinearLayoutManager.HORIZONTAL, true));
        patientProgressBinding.patientRecyclerViewPhotos.setHasFixedSize(true);

        mAllImageHands = new ArrayList<>();
        adapterAllPhotos = new Adapter_All_Photos(getActivity(), mAllImageHands);

        patientProgressBinding.patientRecyclerViewPhotos.setAdapter(adapterAllPhotos);
        adapterAllPhotos.setOnItemClickListener(this);

        mAllImageHands.add(new ModelPatientImageHand("1", "data:image/jpeg;base64,"));
        mAllImageHands.add(new ModelPatientImageHand("2", "data:image/jpeg;base64,"));
        mAllImageHands.add(new ModelPatientImageHand("3", "data:image/jpeg;base64,"));
        mAllImageHands.add(new ModelPatientImageHand("4", "data:image/jpeg;base64,"));
        mAllImageHands.add(new ModelPatientImageHand("5", getResources().getDrawable(R.drawable.doctor_m).toString()));
        adapterAllPhotos.notifyDataSetChanged();


        return patientProgressBinding.getRoot();
    }

    @Override
    public void onItem_Product_Click(int position) {
        Toast.makeText(getActivity(), "" + position, Toast.LENGTH_SHORT).show();
    }
}